import {
  Button,
  FormControl,
  InputLabel,
  MenuItem,
  Modal,
  Select,
  TextField,
} from "@mui/material";
import axios from "axios";
import { useState } from "react";

const AddCoin = ({ open }) => {
  const [state, setState] = useState({
    datas: [],
  });

  const [value, setValue] = useState({
    coin: "",
    price: null,
  });

  const [validation, setValidation] = useState({
    coin: "",
    price: null,
  });

  const saveData = (values) => {
    axios
      .post("http://localhost:3004/in/inproducts", values)
      .then((x) => {
        let old = { ...state };
        old.datas = [x.data, ...old.datas];
        setState(old);
      })
      .catch((err) => console.log(err));
  };

  const handelSubmit = (e) => {
    e.preventDefault();
    // console.log(value);
    saveData(value);

    if (validateForm()) {
      saveData(value);
      setValue({
        coin: "",
        price: "",
        quantity: "",
      });
    } else {
      console.log("Form is Empty");
    }
  };

  const handelInput = (e) => {
    setValue({ ...value, [e.target.name]: e.target.value });
    console.log(e.target.value);
    setValidation({ ...validation, [e.target.name]: "" });
  };

  const validateForm = () => {
    let isValid = true;
    const newValidation = {};
    if (!value.coin) {
      newValidation.coin = " ";
      isValid = false;
    }
    setValidation(newValidation);
    return isValid;
  };

  return (
    <div
      style={{
        height: "100vh",
        width: "100%",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <Modal
        open={open}
        style={{
          background: "white",
          height: "75%",
          width: "40%",
          borderRadius: 10,
          border: "1px solid black",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <div
          style={{
            height: "80%",
            width: "80%",
            padding: 10,
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <div
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              flex: 1,
              width: "100%",
              borderBottom: "1px solid black",
            }}
          >
            <h1 style={{ color: "orange" }}> COIN NAME</h1>
          </div>
          <div
            style={{
              display: "flex",
              flex: 6,
              width: "100%",
              flexDirection: "column",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <TextField
              fullWidth
              type="text"
              name="coin"
              label="COIN NAME"
              variant="outlined"
              value={value.coin || ""}
              onChange={handelInput}
              error={!!validation.coin}
              helperText={validation.coin}
            />
            <TextField
              fullWidth
              type="number"
              name="price"
              label="PRICE"
              variant="outlined"
              value={value.coin || ""}
              onChange={handelInput}
              error={!!validation.price}
              helperText={validation.price}
            />
            <TextField
              fullWidth
              type="number"
              name="quantity"
              label="QUANTITY"
              variant="outlined"
              value={value.coin || ""}
              onChange={handelInput}
              error={!!validation.quantity}
              helperText={validation.quantity}
            />
            <Button
              style={{
                marginTop: 30,
                width: "100%",
                color: "#fff",
                background: "#4F709C",
              }}
              type="submit"
              onClick={handelSubmit}
            >
              Send
            </Button>
            <Button
              style={{
                marginTop: 30,
                width: "100%",
                color: "#fff",
                background: "#4F709C",
              }}
              type="submit"
              onClick={handelSubmit}
            >
              cancle
            </Button>
          </div>
        </div>
      </Modal>
    </div>
  );
};

export default AddCoin;
