// LoginComponent.jsx
import { Button, TextField } from "@mui/material";
import React, { useState } from "react";

const LoginComponent = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = () => {
    if (username === "sumon" && password === "12345678") {
      onLogin();
    } else {
      alert("Incurrect username or password");
    }
  };

  return (
    <div
      style={{
        height: "100vh",
        width: "100%",
        background: "skyblue",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <div
        style={{
          height: "50%",
          width: "50%",
          background: "white",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          borderRadius: 10,
          gap: 10,
        }}
      >
        <div
          style={{
            height: "90%",
            width: "80%",
            borderRadius: 5,
            border: "1px solid black",
          }}
        >
          <h2>Login</h2>
          <TextField
            label="User Name"
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <TextField
            label="Password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <Button
            type="button"
            onClick={handleLogin}
            style={{ width: "29%", background: "skyblue", color: "white" }}
          >
            Login
          </Button>
        </div>
      </div>
    </div>
  );
};

export default LoginComponent;
