import React, { useEffect, useState } from "react";
import BinanceTable from "./BinanceTable";
import { TextField } from "@mui/material";
import { binanceManager } from "../api";

const BinanceHome = () => {
  // const [state, setState] = useState([true, true, true, true]);
  const [state, setState] = useState({
    datas: [],
    dataResults: [],
    searchItem: "",
    butttonState: [true, true],
    modalOpen: false,
  });

  useEffect(() => {
    getData();
  }, []);

  const getData = () => {
    binanceManager
      .getBinanceCoins()
      .then((data) =>
        setState((old) => ({ ...old, datas: data, dataResults: data }))
      )
      .catch((err) => console.log(err));
  };

  const handelModalOpen = () => {
    const old = { ...state };
    old.modalOpen = true;
    setState(old);
  };

  // const handleLogin = () => {
  //   const old = { ...state };
  //   old.loggedIn = true;
  //   getData();
  //   setState(old);
  // };

  const sortedData = [...state.datas].sort((a, b) =>
    a.name.localeCompare(b.name)
  );

  const totalBuyingPrice = state.datas.reduce(
    (acc, item) => acc + item.buying_amount,
    0
  );
  const totalSeelingPrice = state.datas.reduce(
    (acc, item) => acc + item.selling_amount,
    0
  );
  const totalGridAmount = state.datas.reduce(
    (acc, item) => acc + item.grid_amount,
    0
  );
  const totalGriQuantity = state.datas.reduce(
    (acc, item) => acc + item.grid_quantity,
    0
  );

  // const minimumProfit = dstate.datas.reduce(
  //   (acc, item) => item.selling_amount * 0.01
  // );

  // const handleChange = (val, id) => {
  //   let item = [...dstate.datas].filter((x) => x._id === id)[0];
  //   item.auto_buying = !item.auto_buying;

  //   dstate.datas.indexOf(item);
  //   // setDstate(old=>({...dstate,datas:}))
  // };

  const len = state.datas.length;
  console.log(len);

  const searchText = (e) => {
    let searchTxt = e.target.value;
    let old = { ...state };
    if (searchTxt === "") {
      old.datas = [...old.dataResults];
    } else {
      old.datas = [...old.dataResults].filter((x) => {
        return x.name && x.name.toLowerCase().includes(searchTxt.toLowerCase());
      });
    }
    old.searchItem = searchTxt;

    console.log(old.searchItem);

    setState(old);
  };

  const handleChange = () => {
    alert("Button Clicked");
  };

  return (
    <div style={{ height: "100vh", width: "100%", position: "relative" }}>
      <>
        <div
          style={{
            height: "15%",
            width: "100%",
            boxShadow:
              "rgba(0, 0, 0, 0.07) 0px 1px 2px, rgba(0, 0, 0, 0.07) 0px 2px 4px, rgba(0, 0, 0, 0.07) 0px 4px 8px, rgba(0, 0, 0, 0.07) 0px 8px 16px, rgba(0, 0, 0, 0.07) 0px 16px 32px, rgba(0, 0, 0, 0.07) 0px 32px 64px",
          }}
        >
          <div
            style={{
              height: 40,
              width: 130,
              position: "absolute",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              top: 20,
              right: 20,
              borderRadius: 10,
              boxShadow:
                "rgba(0, 0, 0, 0.07) 0px 1px 2px, rgba(0, 0, 0, 0.07) 0px 2px 4px, rgba(0, 0, 0, 0.07) 0px 4px 8px, rgba(0, 0, 0, 0.07) 0px 8px 16px, rgba(0, 0, 0, 0.07) 0px 16px 32px, rgba(0, 0, 0, 0.07) 0px 32px 64px",
            }}
          >
            <h4>Total Coin :</h4> <h3 style={{ marginLeft: 5 }}>{len}</h3>
          </div>
          <TextField
            onChange={searchText}
            value={state.searchItem}
            label="Search..."
            type="search"
            style={{
              textDecoration: "none",
              position: "absolute",
              right: "12%",
              top: 20,
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              borderRadius: 10,
              boxShadow:
                "rgba(0, 0, 0, 0.07) 0px 1px 2px, rgba(0, 0, 0, 0.07) 0px 2px 4px, rgba(0, 0, 0, 0.07) 0px 4px 8px, rgba(0, 0, 0, 0.07) 0px 8px 16px, rgba(0, 0, 0, 0.07) 0px 16px 32px, rgba(0, 0, 0, 0.07) 0px 32px 64px",
            }}
          />
          <div
            style={{
              height: 70,
              width: 220,
              position: "absolute",
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "space-around",
              top: 15,
              left: "5%",
              borderRadius: 5,
              boxShadow:
                "rgba(0, 0, 0, 0.07) 0px 1px 2px, rgba(0, 0, 0, 0.07) 0px 2px 4px, rgba(0, 0, 0, 0.07) 0px 4px 8px, rgba(0, 0, 0, 0.07) 0px 8px 16px, rgba(0, 0, 0, 0.07) 0px 16px 32px, rgba(0, 0, 0, 0.07) 0px 32px 64px",
            }}
          >
            <p>Total Grid Amount :</p>
            <h2 style={{ marginLeft: 10 }}>{totalGridAmount.toFixed(3)}</h2>
          </div>
          <div
            style={{
              height: 70,
              width: 200,
              position: "absolute",
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "space-around",
              top: 15,
              left: "20%",
              borderRadius: 5,
              boxShadow:
                "rgba(0, 0, 0, 0.07) 0px 1px 2px, rgba(0, 0, 0, 0.07) 0px 2px 4px, rgba(0, 0, 0, 0.07) 0px 4px 8px, rgba(0, 0, 0, 0.07) 0px 8px 16px, rgba(0, 0, 0, 0.07) 0px 16px 32px, rgba(0, 0, 0, 0.07) 0px 32px 64px",
            }}
          >
            <p>Total Selleing amount :</p>{" "}
            <h2>{totalSeelingPrice.toFixed(3)}</h2>
          </div>
          <div
            style={{
              height: 70,
              width: 200,
              position: "absolute",
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "space-around",
              top: 15,
              left: "34%",
              borderRadius: 5,
              boxShadow:
                "rgba(0, 0, 0, 0.07) 0px 1px 2px, rgba(0, 0, 0, 0.07) 0px 2px 4px, rgba(0, 0, 0, 0.07) 0px 4px 8px, rgba(0, 0, 0, 0.07) 0px 8px 16px, rgba(0, 0, 0, 0.07) 0px 16px 32px, rgba(0, 0, 0, 0.07) 0px 32px 64px",
            }}
          >
            <p>Total Buying Amount : </p> <h2>{totalBuyingPrice.toFixed(3)}</h2>
          </div>

          <div
            style={{
              height: 70,
              width: 220,
              position: "absolute",
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "space-around",
              top: 15,
              left: "48%",
              borderRadius: 5,
              boxShadow:
                "rgba(0, 0, 0, 0.07) 0px 1px 2px, rgba(0, 0, 0, 0.07) 0px 2px 4px, rgba(0, 0, 0, 0.07) 0px 4px 8px, rgba(0, 0, 0, 0.07) 0px 8px 16px, rgba(0, 0, 0, 0.07) 0px 16px 32px, rgba(0, 0, 0, 0.07) 0px 32px 64px",
            }}
          >
            <p>Total Grid Quantity :</p>{" "}
            <h2 style={{ marginLeft: 10 }}>{totalGriQuantity.toFixed(3)}</h2>
          </div>
        </div>
        <div
          style={{
            height: "85%",
            width: "100%",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <div
            style={{
              height: "96%",
              width: "100%",
              display: "flex",
              flexDirection: "column",
              justifyContent: "space-around",
              alignItems: "center",
            }}
          >
            <div
              style={{
                height: "100%",
                width: "100%",
                overflowY: "scroll",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                gap: 10,
                marginTop: 3,
              }}
            >
              {sortedData.map((item, i) => (
                <BinanceTable key={i} item={item} handleChange={handleChange} />
              ))}
            </div>
          </div>
        </div>
      </>
    </div>
  );
};

export default BinanceHome;
