import {
  Button,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  TextField,
} from "@mui/material";
import { useState } from "react";

const Main = () => {
  const [value, setValue] = useState({
    coin: "",
    price: null,
  });

  const handelSubmit = (e) => {
    e.preventDefault();
    console.log(value);
  };

  const handelInput = (e) => {
    setValue({ ...value, [e.target.name]: e.target.value });
    console.log(e.target.value);
    setValidation({ ...validation, [e.target.name]: "" });
  };

  const COIN = [
    "BNB",
    "USDT",
    "BTC",
    "ETH",
    "USDC",
    "ORDI",
    "MEME",
    "TIA",
    "TVK",
    "XRP",
    "STEEM",
  ];

  const [validation, setValidation] = useState({
    coin: "",
    price: null,
  });

  const validateForm = () => {
    let isValid = true;
    const newValidation = {};

    if (!value.coin) {
      newValidation.coin = " ";
      isValid = false;
    }

    if (!value.price) {
      newValidation.price = " ";
      isValid = false;
    }

    setValidation(newValidation);
    return isValid;
  };

  const onSizeChange = (e) => {
    setValue((old) => ({ ...old, [e.target.name]: e.target.value }));
  };

  return (
    <div
      style={{
        height: "100vh",
        width: "100%",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "#61A3BA",
      }}
    >
      <div
        style={{
          height: "70vh",
          width: "40%",
          borderRadius: 10,
          border: "1px solid orange",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          background: "#E0F4FF",
        }}
      >
        <div
          style={{
            height: "70%",
            width: "80%",
            padding: 10,
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <h1 style={{ marginTop: -30 }}>FILL UP THE FORM</h1>
          <FormControl fullWidth style={{ background: "gray" }}>
            <InputLabel>SELECT COIN NAME</InputLabel>
            <Select
              value={value.coin}
              label="SELECT COIN NAME"
              onChange={onSizeChange}
              name="coin"
            >
              {COIN.map((x, i) => (
                <MenuItem selected="USD" key={i} value={x}>
                  {x}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          <TextField
            fullWidth
            id="outlined-basic"
            type="number"
            name="price"
            label="BUYING PRICE"
            variant="outlined"
            value={value.price}
            onChange={handelInput}
            style={{ marginTop: 30 }}
            error={!!validation.price}
            helperText={validation.price}
          />

          <Button
            style={{
              marginTop: 30,
              background: "#87C4FF",
              width: "100%",
              color: "white",
            }}
            type="submit"
            onClick={handelSubmit}
          >
            Send
          </Button>
        </div>
      </div>
    </div>
  );
};

export default Main;
