import { Button, Modal, Switch, TextField } from "@mui/material";
import React, { useEffect, useState } from "react";

const EditModal = ({ open, handelModalClose, selectItem }) => {
  const [value, setValue] = useState({
    auto_buying: false,
    auto_selling: false,
    buying_restriction_limit: 0,
  });

  useEffect(() => {
    setValue({ ...selectItem });
  }, [selectItem]);

  const handelSubmit = (e) => {
    e.preventDefault();
    console.log(value);
  };
  const handelInput = (e) => {
    console.log(e.target.value);
    setValue({ ...value, [e.target.name]: e.target.value });
  };
  const handleSwitchChange = (name) => {
    setValue({ ...value, [name]: !value[name] });
  };
  return (
    <Modal
      open={open}
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <div
        style={{
          height: "60%",
          width: "50%",
          borderRadius: 10,
          border: "1px solid black",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          background: "white",
        }}
      >
        <div
          style={{
            height: "80%",
            width: "80%",
            padding: 10,
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <div
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              flex: 1,
              width: "100%",
              height: "10%",
              borderBottom: "1px solid white",
            }}
          >
            <h1 style={{ color: "orange" }}> Update Price</h1>
          </div>
          <div
            style={{
              display: "flex",
              flex: 6,
              width: "100%",
              height: "80%",
              flexDirection: "column",
              justifyContent: "center",
              alignItems: "center",
              gap: 20,
            }}
          >
            <p
              style={{
                width: "50%",
                height: "20%",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                boxShadow:
                  "rgba(0, 0, 0, 0.07) 0px 1px 2px, rgba(0, 0, 0, 0.07) 0px 2px 4px, rgba(0, 0, 0, 0.07) 0px 4px 8px, rgba(0, 0, 0, 0.07) 0px 8px 16px, rgba(0, 0, 0, 0.07) 0px 16px 32px, rgba(0, 0, 0, 0.07) 0px 32px 64px",
              }}
            >
              AUTO BUYING
              {
                <Switch
                  value={value.auto_buying}
                  name="auto_buying"
                  // onChange={() => handleSwitchChange("auto_buying")}
                  onChange={handelInput}
                />
              }
            </p>
            <p
              style={{
                width: "50%",
                height: "20%",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                boxShadow:
                  "rgba(0, 0, 0, 0.07) 0px 1px 2px, rgba(0, 0, 0, 0.07) 0px 2px 4px, rgba(0, 0, 0, 0.07) 0px 4px 8px, rgba(0, 0, 0, 0.07) 0px 8px 16px, rgba(0, 0, 0, 0.07) 0px 16px 32px, rgba(0, 0, 0, 0.07) 0px 32px 64px",
              }}
            >
              AUTO SELLING
              {
                <Switch
                  value={value.auto_selling}
                  name="auto_selling"
                  onChange={handelInput}
                  // onChange={() => handleSwitchChange("auto_selling")}
                />
              }
            </p>
            <TextField
              style={{ width: "50%" }}
              type="number"
              name="buying_restriction_limit"
              label="BUYING RESTRICTION LIMIT"
              variant="outlined"
              onChange={handelInput}
              value={value.buying_restriction_limit}
            />
          </div>
          <div
            style={{
              height: "10%",
              width: "50%",
            }}
          >
            <Button
              style={{
                width: "10%",
                color: "#fff",
                background: "#4F709C",
              }}
              type="submit"
              onClick={handelSubmit}
            >
              send
            </Button>
            <Button
              style={{
                width: "10%",
                color: "#fff",
                background: "#4F709C",
                marginLeft: 20,
              }}
              type="submit"
              onClick={handelModalClose}
            >
              close
            </Button>
          </div>
        </div>
      </div>
    </Modal>
  );
};

export default EditModal;
