import React, { useState } from "react";
import { Button, Modal } from "@mui/material";

const StateModal = ({
  open,
  handelStateModalClose,
  selectItem,
  exitFromCoin,
}) => {
  const handleYesClick = (e) => {
    e.preventDefault();
    exitFromCoin(selectItem);
  };
  return (
    <Modal
      open={open}
      style={{
        height: "100%",
        width: "100%",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <div
        style={{
          background: "white",
          height: 150,
          width: 350,
          padding: 10,
          borderRadius: 10,
          display: "flex",
          flexDirection: "column",
        }}
      >
        <div style={{ flex: 8 }}>
          <h3 style={{ color: "blue", textTransform: "uppercase" }}>
            Do You Want To Exit From{" "}
            <span style={{ color: "red", textAlign: "center" }}>
              {" "}
              {selectItem.name}
            </span>
          </h3>
          {/* <p>{selectItem}</p> */}
        </div>
        <div style={{ flex: 2, display: "flex", gap: 10 }}>
          <Button
            style={{
              color: "white",
              background: "gray",
            }}
            onClick={handleYesClick}
          >
            YES
          </Button>
          <Button
            style={{
              color: "white",
              background: "gray",
            }}
            onClick={handelStateModalClose}
          >
            NO
          </Button>
        </div>
      </div>
    </Modal>
  );
};
export default StateModal;
