import React, { useEffect, useState } from "react";
import BinanceTable from "./BinanceTable";
import { Button, TextField } from "@mui/material";
import { binanceManager } from "../api";
import EditModal from "./EditModal";
import TopSubDiv from "./TopSubDiv";
import "./main.css";

const BinanceHome = () => {
  const [state, setState] = useState({
    datas: [],
    dataResults: [],
    searchItem: "",
    modalOpen: false,
    selectItem: null,
  });

  useEffect(() => {
    getData();
  }, []);

  const getData = () => {
    binanceManager
      .getBinanceCoins()
      .then((data) =>
        setState((old) => ({
          ...old,
          datas: data.sort((a, b) => a.name.localeCompare(b.name)),
          dataResults: data,
        }))
      )
      .catch((err) => console.log(err));
  };

  const updateItem = (item) => {
    binanceManager
      .updateCoin(item)
      .then((updatedItem) => {
        let arr = [...state.datas];
        let oldItem = arr.filter((x) => x._id === item._id)[0];
        arr[arr.indexOf(oldItem)] = updatedItem;
        setState((old) => ({ ...old, datas: arr, modalOpen: false }));
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const handelEditModal = (item) => {
    const old = { ...state };
    old.modalOpen = true;
    old.selectItem = item;
    setState(old);
  };

  const handelModalClose = () => {
    const old = { ...state };
    old.modalOpen = false;
    setState(old);
  };

  const totalBuyingPrice = state.datas.reduce(
    (acc, item) => acc + item.buying_amount,
    0
  );
  const totalSeelingPrice = state.datas.reduce(
    (acc, item) => acc + item.selling_amount,
    0
  );
  const totalGridAmount = state.datas.reduce(
    (acc, item) => acc + item.grid_amount,
    0
  );

  const maximumFrofit = totalSeelingPrice + totalGridAmount - totalBuyingPrice;

  const searchText = (e) => {
    let searchTxt = e.target.value;
    let old = { ...state };
    if (searchTxt === "") {
      old.datas = [...old.dataResults];
    } else {
      old.datas = [...old.dataResults].filter((x) => {
        return x.name && x.name.toLowerCase().includes(searchTxt.toLowerCase());
      });
    }
    old.searchItem = searchTxt;

    setState(old);
  };

  const handleChange = () => {
    alert("Button Click");
  };

  return (
    <div style={{ height: "100vh", width: "100%", position: "relative" }}>
      <>
        <div
          style={{
            height: "15%",
            width: "100%",
            display: "flex",
            boxShadow:
              "rgba(0, 0, 0, 0.07) 0px 1px 2px, rgba(0, 0, 0, 0.07) 0px 2px 4px, rgba(0, 0, 0, 0.07) 0px 4px 8px, rgba(0, 0, 0, 0.07) 0px 8px 16px, rgba(0, 0, 0, 0.07) 0px 16px 32px, rgba(0, 0, 0, 0.07) 0px 32px 64px",
          }}
        >
          <div
            style={{
              height: "100%",
              width: "70%",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            <div
              style={{
                height: "50%",
                width: "95%",
                display: "flex",
                alignItems: "center",
                justifyContent: "space-around",
              }}
            >
              <TopSubDiv
                name="TOTAL GRID AMOUNT"
                price={totalGridAmount.toFixed(3)}
              />
              <TopSubDiv
                name="TOTAL SELLING AMOUNT"
                price={totalSeelingPrice.toFixed(3)}
              />
              <TopSubDiv
                name="TOTAL BUYING AMOUNT"
                price={totalBuyingPrice.toFixed(3)}
              />
              <TopSubDiv
                name="MINIMUM PROFIT"
                price={(totalSeelingPrice * 0.01).toFixed(3)}
              />
              <TopSubDiv
                name="MAXIMUM PROFIT"
                price={maximumFrofit.toFixed(4)}
              />
            </div>
          </div>
          <div
            style={{
              height: "100%",
              width: "30%",
              display: "flex",
              alignItems: "center",
              justifyContent: "space-around",
            }}
          >
            <TopSubDiv name="TOTAL COINS" price={state.datas.length} />
            <div
              style={{
                height: 54,
                width: "70%",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
              }}
            >
              <TextField
                onChange={searchText}
                value={state.searchItem}
                label="Search..."
                type="search"
                style={{
                  textDecoration: "none",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  borderRadius: 10,
                  boxShadow:
                    "rgba(0, 0, 0, 0.07) 0px 1px 2px, rgba(0, 0, 0, 0.07) 0px 2px 4px, rgba(0, 0, 0, 0.07) 0px 4px 8px, rgba(0, 0, 0, 0.07) 0px 8px 16px, rgba(0, 0, 0, 0.07) 0px 16px 32px, rgba(0, 0, 0, 0.07) 0px 32px 64px",
                }}
              />
            </div>
          </div>
        </div>
        <div
          style={{
            height: "85%",
            width: "100%",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <div
            style={{
              height: "96%",
              width: "100%",
              display: "flex",
              flexDirection: "column",
              justifyContent: "space-around",
              alignItems: "center",
            }}
          >
            <div
              style={{
                height: "100%",
                width: "100%",
                overflowY: "scroll",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                gap: 9,
                padding: 5,
              }}
            >
              {state.datas.map((item, i) => (
                <BinanceTable
                  key={i}
                  item={item}
                  handleChange={handleChange}
                  handelEditModal={handelEditModal}
                />
              ))}
            </div>
          </div>
          <div
            style={{
              height: 50,
              width: 50,
              position: "absolute",
              right: 50,
              bottom: 40,
              background: "red",
              borderRadius: 10,
            }}
          ></div>
        </div>
      </>
      {state.modalOpen && (
        <EditModal
          updateItem={updateItem}
          selectItem={state.selectItem}
          open={state.modalOpen}
          handelModalClose={handelModalClose}
        />
      )}
    </div>
  );
};

export default BinanceHome;
