import { Button, TextField } from "@mui/material";
import React, { useState } from "react";

const LoginComponent = ({ onLogin }) => {
  // const [username, setUsername] = useState("");
  // const [password, setPassword] = useState("");

  const [loginState, setLogInState] = useState({
    username: "",
    password: "",
  });

  const handleLogin = () => {
    if (
      loginState.username === "forbit@binance.com" &&
      loginState.password === "12345678"
    ) {
      onLogin();
    } else {
      alert("Incurrect username or password");
    }
  };

  return (
    <div
      style={{
        height: "100vh",
        width: "100%",
        background: "skyblue",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <div
        style={{
          height: "50%",
          width: "50%",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          borderRadius: 10,
        }}
      >
        <div
          style={{
            height: "90%",
            width: "60%",
            borderRadius: 5,
            border: "1px solid black",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            gap: 20,
          }}
        >
          <h2>Login</h2>
          <TextField
            label="User Name"
            type="text"
            value={loginState.username}
            style={{ width: "75%" }}
            onChange={(e) => setLogInState(e.target.value)}
          />
          <TextField
            label="Password"
            type="password"
            value={loginState.password}
            style={{ width: "75%" }}
            onChange={(e) => setLogInState(e.target.value)}
          />
          <Button
            type="button"
            onClick={handleLogin}
            style={{ width: "75%", background: "#3887BE", color: "white" }}
          >
            Login
          </Button>
        </div>
      </div>
    </div>
  );
};

export default LoginComponent;
