import React, { useEffect, useState } from "react";
import BinanceTable from "./BinanceTable";
import axios from "axios";

const BinanceHome = () => {
  const [state, setState] = useState([true, true, true, true]);
  const [dstate, setDstate] = useState({
    datas: [],
    butttonState: [true, true],
  });

  useEffect(() => {
    getData();
  }, []);

  const getData = () => {
    axios
      .get("https://forbit.tech/sohel/sohel/coins")
      .then((res) => {
        const data = res.data;
        const old = { ...dstate };
        old.datas = data;
        console.log(data);
        setDstate(old);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div style={{ height: "100vh", width: "100%" }}>
      <div
        style={{
          height: "10%",
          width: "100%",
          boxShadow:
            "rgba(0, 0, 0, 0.07) 0px 1px 2px, rgba(0, 0, 0, 0.07) 0px 2px 4px, rgba(0, 0, 0, 0.07) 0px 4px 8px, rgba(0, 0, 0, 0.07) 0px 8px 16px, rgba(0, 0, 0, 0.07) 0px 16px 32px, rgba(0, 0, 0, 0.07) 0px 32px 64px",
        }}
      >
        <p>{dstate.datas.name[0]}</p>
      </div>
      <div
        style={{
          height: "90%",
          width: "100%",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <div
          style={{
            height: "95%",
            width: "100%",
            display: "flex",
            flexDirection: "column",
            justifyContent: "space-around",
            alignItems: "center",
          }}
        >
          <div
            style={{
              height: "100%",
              width: "100%",
              overflowY: "scroll",
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              gap: 10,
            }}
          >
            {dstate.datas.map((item, i) => (
              <BinanceTable key={i} item={item} />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default BinanceHome;
