import {
  Button,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  TextField,
} from "@mui/material";
import axios from "axios";
import { useState } from "react";

const Main = () => {
  const [value, setValue] = useState({
    coin: "",
    price: null,
  });

  const [state, setState] = useState({
    datas: [],
  });

  const saveData = (values) => {
    axios
      .post("http://localhost:3004/in/inproducts", values)
      .then((x) => {
        let old = { ...state };
        old.datas = [x.data, ...old.datas];
        setState(old);
      })
      .catch((err) => console.log(err));
  };

  const handelSubmit = (e) => {
    e.preventDefault();
    console.log(value);
    saveData(value);

    if (validateForm()) {
      saveData(value);

      setValue({
        coin: "",
        price: "",
      });
    } else {
      console.log("Form is Empty");
    }
  };

  const handelInput = (e) => {
    setValue({ ...value, [e.target.name]: e.target.value });
    console.log(e.target.value);
    setValidation({ ...validation, [e.target.name]: "" });
  };

  const COIN = [
    "BNB",
    "USDT",
    "BTC",
    "ETH",
    "USDC",
    "ORDI",
    "MEME",
    "TIA",
    "TVK",
    "XRP",
    "STEEM",
  ];

  const [validation, setValidation] = useState({
    coin: "",
    price: null,
  });

  const validateForm = () => {
    let isValid = true;
    const newValidation = {};

    if (!value.coin) {
      newValidation.coin = " ";
      isValid = false;
    }

    if (!value.price) {
      newValidation.price = " ";
      isValid = false;
    }

    setValidation(newValidation);
    return isValid;
  };

  const onSizeChange = (e) => {
    setValue((old) => ({ ...old, [e.target.name]: e.target.value }));
  };

  return (
    <div
      style={{
        height: "100vh",
        width: "100%",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "#F0F0F0",
      }}
    >
      <div
        style={{
          height: "70vh",
          width: "40%",
          borderRadius: 10,
          border: "1px solid black",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <div
          style={{
            height: "70%",
            width: "80%",
            padding: 10,
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <div style={{ display: "flex", flex: 1 }}>
            <h1 style={{ color: "orange" }}> COIN PRICE</h1>
          </div>
          <div
            style={{
              display: "flex",
              flex: 8,
              width: "100%",
              flexDirection: "column",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <FormControl
              error={!!validation.coin}
              helperText={validation.coin}
              fullWidth
              style={{}}
            >
              <InputLabel>SELECT COIN NAME</InputLabel>
              <Select
                value={value.coin || ""}
                label="SELECT COIN NAME"
                onChange={onSizeChange}
                name="coin"
              >
                {COIN.map((x, i) => (
                  <MenuItem key={i} value={x}>
                    {x}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <TextField
              fullWidth
              type="number"
              name="price"
              label="BUYING PRICE"
              variant="outlined"
              value={value.price || ""}
              onChange={handelInput}
              style={{ marginTop: 30 }}
              error={!!validation.price}
              helperText={validation.price}
            />
            <Button
              style={{
                marginTop: 30,
                width: "100%",
                color: "#fff",
                background: "#4F709C",
              }}
              type="submit"
              onClick={handelSubmit}
            >
              Send
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Main;
