import { Button, TextField } from "@mui/material";
import { useState } from "react";

const Main = () => {
  const [value, setValue] = useState({
    coin: "",
    price: null,
  });

  const handelSubmit = (e) => {
    e.preventDefault();
    console.log(value);
  };

  const handelInput = (e) => {
    setValue({ ...value, [e.target.name]: e.target.value });
    console.log(e.target.value);
  };

  return (
    <div
      style={{
        height: "100vh",
        width: "100%",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div
        style={{
          height: "70vh",
          width: "40%",
          borderRadius: 10,
          border: "1px solid orange",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <div
          style={{
            height: "70%",
            width: "80%",
            padding: 10,
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <h1 style={{ marginTop: -30 }}>FILL UP THE FORM</h1>
          <TextField
            fullWidth
            type="text"
            name="coin"
            id="outlined-basic"
            label="SELECT COIN"
            variant="outlined"
            value={value.coin}
            onChange={handelInput}
          />
          <TextField
            fullWidth
            id="outlined-basic"
            type="number"
            name="price"
            label="BUYING PRICE"
            variant="outlined"
            value={value.price}
            onChange={handelInput}
            style={{ marginTop: 40 }}
          />

          <FormControl
            fullWidth
            style={{ marginTop: 15, marginBottom: 10 }}
            error={!!validation.size}
            helperText={validation.size}
          >
            <InputLabel>Select Size</InputLabel>
            <Select
              value={product.size ? product.size : ""}
              label="Select Size"
              onChange={onSizeChange}
              name="size"
            >
              {SIZE.map((x, i) => (
                <MenuItem key={i} value={x}>
                  {x}
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          <Button
            style={{
              marginTop: 30,
              background: "silver",
              width: "100%",
              color: "black",
            }}
            type="submit"
            onClick={handelSubmit}
          >
            Send
          </Button>
        </div>
      </div>
    </div>
  );
};

export default Main;
