import { Button, TextField } from "@mui/material";
import React, { useState } from "react";

const LoginComponent = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = () => {
    if (username === "forbit" && password === "12345") {
      onLogin();
    } else {
      alert("Incurrect username or password");
    }
  };

  return (
    <div
      style={{
        height: "100vh",
        width: "100%",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <div
        style={{
          height: "50%",
          // width: "50%",
          width: {
            xs: 100, //0
            sm: 200, //600
            md: 300, //900
            lg: 400, //1200
            xl: 500, //1536
          },
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          borderRadius: 10,
        }}
      >
        <div
          style={{
            height: "90%",
            width: "60%",
            borderRadius: 5,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            gap: 20,
            borderBottom: "1px solid silver",
            boxShadow:
              "rgba(50, 50, 93, 0.25) 0px 30px 60px -12px inset, rgba(0, 0, 0, 0.3) 0px 18px 36px -18px inset",
          }}
        >
          <h2 style={{ color: "black" }}>LOG IN</h2>
          <TextField
            label="User Name"
            type="text"
            value={username}
            style={{ width: "75%" }}
            onChange={(e) => setUsername(e.target.value)}
          />
          <TextField
            label="Password"
            type="password"
            value={password}
            style={{ width: "75%" }}
            onChange={(e) => setPassword(e.target.value)}
          />
          <Button
            type="button"
            onClick={handleLogin}
            style={{ width: "75%", background: "#3887BE", color: "white" }}
          >
            Login
          </Button>
        </div>
      </div>
    </div>
  );
};

export default LoginComponent;
