import { Button, Modal, Switch, TextField } from "@mui/material";
import React, { useEffect, useState } from "react";

const EditModal = ({ open, handelModalClose, selectItem, updateItem }) => {
  const [state, setState] = useState({ ...selectItem });

  useEffect(() => {
    setState({ ...selectItem });
  }, [selectItem]);

  const handelSubmit = (e) => {
    e.preventDefault();
    updateItem(state);
  };

  const handelInput = (e) => {
    setState({ ...state, [e.target.name]: e.target.value });
  };

  const handleSwitchChange = (name) => {
    setState((prevValue) => ({ ...prevValue, [name]: !prevValue[name] }));
  };

  return (
    <Modal
      open={open}
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <div
        style={{
          height: "60%",
          width: "50%",
          borderRadius: 10,
          border: "1px solid black",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          background: "white",
        }}
      >
        <div
          style={{
            height: "80%",
            width: "80%",
            padding: 10,
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <div
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              flex: 1,
              width: "100%",
              height: "10%",
              borderBottom: "1px solid white",
            }}
          >
            <p style={{ color: "orange", fontSize: 22, fontWeight: "bolder" }}>
              UPDATE PRICE OF {"    "}
              <span style={{ color: "red", marginLeft: 5 }}>
                "{state.name}"
              </span>
            </p>
          </div>
          <div
            style={{
              display: "flex",
              flex: 6,
              width: "100%",
              height: "80%",
              flexDirection: "column",
              justifyContent: "center",
              alignItems: "center",
              gap: 20,
            }}
          >
            <p
              style={{
                width: "50%",
                height: "20%",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                boxShadow:
                  "rgba(0, 0, 0, 0.07) 0px 1px 2px, rgba(0, 0, 0, 0.07) 0px 2px 4px, rgba(0, 0, 0, 0.07) 0px 4px 8px, rgba(0, 0, 0, 0.07) 0px 8px 16px, rgba(0, 0, 0, 0.07) 0px 16px 32px, rgba(0, 0, 0, 0.07) 0px 32px 64px",
              }}
            >
              AUTO BUYING
              {
                <Switch
                  value={state.auto_buying}
                  name="auto_buying"
                  onChange={() => handleSwitchChange("auto_buying")}
                  checked={state.auto_buying}
                />
              }
            </p>
            <p
              style={{
                width: "50%",
                height: "20%",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                boxShadow:
                  "rgba(0, 0, 0, 0.07) 0px 1px 2px, rgba(0, 0, 0, 0.07) 0px 2px 4px, rgba(0, 0, 0, 0.07) 0px 4px 8px, rgba(0, 0, 0, 0.07) 0px 8px 16px, rgba(0, 0, 0, 0.07) 0px 16px 32px, rgba(0, 0, 0, 0.07) 0px 32px 64px",
              }}
            >
              AUTO SELLING
              {
                <Switch
                  value={state.auto_selling}
                  name="auto_selling"
                  onChange={() => handleSwitchChange("auto_selling")}
                  checked={state.auto_selling}
                />
              }
            </p>
            <TextField
              style={{ width: "50%" }}
              type="number"
              name="buying_restriction_limit"
              label="BUYING RESTRICTION LIMIT"
              variant="outlined"
              onChange={handelInput}
              value={state.buying_restriction_limit}
            />
          </div>
          <div
            style={{
              height: "10%",
              width: "50%",
            }}
          >
            <Button
              style={{
                width: "13%",
                color: "#fff",
                background: "#4F709C",
                fontSize: 12,
                fontWeight: "bold",
              }}
              type="submit"
              onClick={handelSubmit}
            >
              update
            </Button>
            <Button
              style={{
                width: "10%",
                color: "#fff",
                background: "#4F709C",
                marginLeft: 20,
                fontSize: 12,
                fontWeight: "bold",
              }}
              type="submit"
              onClick={handelModalClose}
            >
              close
            </Button>
          </div>
        </div>
      </div>
    </Modal>
  );
};

export default EditModal;
