// LoginComponent.jsx
import { Button, TextField } from "@mui/material";
import React, { useState } from "react";

const LoginComponent = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = () => {
    if (username === "sumon" && password === "12345678") {
      onLogin();
    } else {
      alert("Incurrect username or password");
    }
  };

  return (
    <div
      style={{
        height: "100vh",
        width: "100%",
        background: "skyblue",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <div
        style={{
          height: "50%",
          width: "50%",
          background: "gray",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          gap: 10,
        }}
      >
        <h2>Login</h2>
        <TextField
          label="Username"
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <TextField
          label="Password"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <Button
          type="button"
          onClick={handleLogin}
          style={{ width: "29%", background: "skyblue", color: "white" }}
        >
          Login
        </Button>
      </div>
    </div>
  );
};

export default LoginComponent;
